package Assignments;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		for (int i=1; i <=10; i++)
		{
			if (i%2==0)
			{
				System.out.println(i + " Is a even number");
			}
			else
				System.out.println(i + " Is a Odd number");
		}
		
	}

}
